{% macro pageheader() %}

<!DOCTYPE html>

<div id="header-image" class="layout-div">

    <div id="print-heading">{{ "SERVICE ORDER" }}</div>

    <!-- Company Information -->
    <table class="layout-table">
        <tbody>
        <tr class="layout-row">
            <td class="layout-cell" id="company-info-cell">
                <div id="company-info">
                    <p>
                        {% set our_address =  frappe.db.get_value("Address", "Eskill Trading Support-Office", "address_line1") ~ ', ' ~ frappe.db.get_value("Address", "Eskill Trading-Office", "address_line2") ~ ', ' ~ frappe.db.get_value("Address", "Eskill Trading-Office", "city") %}
                        {% if our_address and our_address != 'None' %}{{ our_address }}{% else %}118 Enterprise Road, Highlands, Harare{% endif %}
                    </p>
                    <p>
                        {% set our_phone = frappe.db.get_value("Address", "Eskill Trading Support-Office", "phone") %}
                        Phone: {% if our_phone and our_phone != 'None' %}{{ our_phone }}{% else %}+263 8677002000, +263 772148705-8{% endif %}<br>
                        {% set our_email = frappe.db.get_value("Address", "Eskill Trading Support-Office", "email_id") %}
                        Email: {% if our_email and our_email != 'None' %}{{ our_email }}{% else %}support@eskilltrading.com{% endif %}
                    </p>
                </div>
            </td>


            <td class="layout-cell" id="project-name-cell">
                {% if doc.project %}
                    {% set thisproject= frappe.get_doc("Project", doc.project) %}
                    {% set project_style = 'style=\"font-weight: bold; color: ' ~ thisproject.project_colour ~ '; border: 2px solid ' ~ thisproject.project_colour ~ ';\"' | safe %}
                    <table class="layout-table">
                        <tbody><tr class="layout-row">
                            <td class="layout-cell" style="vertical-align: middle;">
                                <div class="project-name" {{ project_style }}>
                                  <span id="project-icon">
                                    {{ projectsvg(thisproject.project_colour) }}
                                  </span>
                                  <span id="project-text">  
                                    {% set project_title_style = 'style=\"color: ' ~ thisproject.project_colour ~ '; font-weight: bold;\"' | safe %}
                                    <p {{ project_title_style }}>PROJECT<br>{{ doc.project | truncate(27, True) }}</p>

                                  </span>
                                </div>
                            </td>
                        </tr></tbody>
                    </table>
                {% endif %}
            </td>

            <!-- Tax Information -->
            <td class="layout-cell" id="tax-info-cell">
                <div id="tax-info">
                    <p>VAT Number: {{ frappe.db.get_value("Company", "Eskill Trading (Pvt) Ltd", "tax_id") }}</p>
                    <p>BP Number: {{ frappe.db.get_value("Company", "Eskill Trading (Pvt) Ltd", "bp_number") }}</p>
                </div>
            </td>
        </tr>
        </tbody>
    </table>
</div>
<div id="top-info">
    <table class="layout-table">
        <tbody>
        <tr class="layout-row">

            <!-- Customer Name and Address -->
            <td class="layout-cell" id="customer-info">
                <div class="black-box">
                    <div class="yellow-bar">
                        Service Provided To
                    </div>
                    <div class="box-content-left" id="customer">
                        {% if doc.customer and doc.customer_name and doc.customer != 'None' %}
                            {% set thiscustomer = frappe.get_doc("Customer", doc.customer) %}
                            <strong>{{ doc.customer_name }}</strong><br>
                        {% elif doc.customer and not doc.customer_name and doc.customer != 'None' %}
                            {% set thiscustomer = frappe.get_doc("Customer", doc.customer) %}
                            {% if thiscustomer %}<strong>{{ thiscustomer.customer_name }}</strong><br>{% endif %}
                        {% endif %}
                        {% if thiscustomer %}
                            {% if thiscustomer.primary_address %}
                                {{ thiscustomer.primary_address }}
                            {% else %}
                                {% set address_links = frappe.get_all('Dynamic Link', filters=[['link_doctype', '=', 'Customer'],['link_name', '=', doc.customer],['parenttype', '=', 'Address']], fields=['parent']) %}
                                {% set cust_address = namespace(line1='', line2='', city='', country='', email='', phone='') %}
                                {% for add_link in address_links %}
                                    {% set thisaddress = frappe.get_doc("Address", add_link.parent) %}
                                    {% set cust_address.line1 = thisaddress.address_line1 %}
                                    {% set cust_address.line2 = thisaddress.address_line2 %}
                                    {% set cust_address.city = thisaddress.city %}
                                    {% set cust_address.country = thisaddress.country %}
                                    {% set cust_address.email = thisaddress.email_id %}
                                    {% set cust_address.phone = thisaddress.phone %}
                                {% endfor %}
                                {% if cust_address.line1 %}{{ cust_address.line1 }}<br>{% endif %}
                                {% if cust_address.line2 %}{{ cust_address.line2 }}<br>{% endif %}
                                {% if cust_address.city %}{{ cust_address.city }}<br>{% endif %}
                                {% if cust_address.country %}{{ cust_address.country }}<br>{% endif %}
                                {% if cust_address.phone %}Phone: {{ cust_address.phone }}<br>{% endif %}
                                {% if cust_address.email %}Email: {{ cust_address.email }}{% endif %}
                            {% endif %}
                        {% endif %}
                    </div>
                </div>
            </td>

            <!-- Document Information -->
            <td class="layout-cell" id="document-info">
                <table class="layout-table">
                    <tbody>
                        <tr class="layout-row">
                            <td class="layout-cell" id="date-cell">
                                <div class="black-box">
                                    <div class="yellow-bar">
                                        Opened
                                    </div>
                                    <div class="box-content-center" id="date">
                                        {% if doc.opening_date and doc.opening_time %}
                                            {{ frappe.utils.get_datetime(doc.opening_date ~ ' ' ~ doc.opening_time).strftime("%d-%m-%Y %I:%M%p").split(' ')[0] }}<br>
                                            {{ frappe.utils.get_datetime(doc.opening_date ~ ' ' ~ doc.opening_time).strftime("%d-%m-%Y %I:%M%p").split(' ')[1] }}
                                        {% endif %}
                                    </div>
                                </div>
                            </td>
                            <td class="layout-cell" id="date-cell">
                                <div class="black-box">
                                    <div class="yellow-bar">
                                        Resolved
                                    </div>
                                    <div class="box-content-center" id="date">
                                        {% if doc.resolution_date %}
                                            {{ frappe.utils.get_datetime(doc.resolution_date).strftime("%d-%m-%Y %I:%M%p").split(' ')[0] }}<br>
                                            {{ frappe.utils.get_datetime(doc.resolution_date).strftime("%d-%m-%Y %I:%M%p").split(' ')[1] }}
                                        {% else %}
                                            Still<br>Active
                                        {% endif %}
                                    </div>
                                </div>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <table class="layout-table">
                    <tbody>
                        <tr class="layout-row">
                            <td class="layout-cell" id="document-number-cell">
                                <div class="black-box">
                                    <div class="yellow-bar">
                                        Issue Number
                                    </div>
                                    <div class="box-content-center" id="document-number">
                                        {% if doc.name %}{{ doc.name }}{% endif %}
                                    </div>
                                </div>
                            </td>                            
                        </tr>                        
                        <tr class="layout-row">
                            <td class="layout-cell" id="other-number-cell">
                                <div class="black-box">

                                        <div class="yellow-bar">Technician</div>
                                        <div class="box-content-center" id="other-number">
                                            {% if doc.technician_name %}{{ doc.technician_name }}{% endif %}                                      
                                        </div>

                                </div>
                            </td>                            
                        </tr>
                    </tbody>
                </table>
            </td>

        </tr>
        </tbody>
    </table>
    <table class="layout-table">
        <tbody>
            <tr class="layout-row">
                <td class="layout-cell" id="customer-vat-cell">
                    <div class="black-box">
                        <div class="yellow-bar">Customer VAT</div>
                        <div class="box-content-center" id="customer-vat">
                            {% if doc.customer and doc.customer != 'None' %}
                                {% set thiscustomer= frappe.get_doc("Customer", doc.customer) %}
                                {{ thiscustomer.tax_id }}
                            {% endif %}
                        </div>
                    </div>                    
                </td>
                <td class="layout-cell" id="customer-ref-cell">
                    <div class="black-box">
                        <div class="yellow-bar">Customer Reference</div>
                        <div class="box-content-center" id="customer-ref">
                            {% if doc.customer_reference %}{{ doc.customer_reference }}{% endif %}
                        </div>
                    </div>                    
                </td>
                <td class="layout-cell" id="issue-type-cell">
                    <div class="black-box">
                        <div class="yellow-bar">Issue Type</div>
                        <div class="box-content-center" id="customer-ref">
                            {% if doc.issue_type %}{{ doc.issue_type }}{% endif %}
                        </div>
                    </div>                    
                </td>                
                <td class="layout-cell" id="salesperson-cell">
                    <div class="black-box">
                        <div class="yellow-bar">Processed By</div>
                        <div class="box-content-center" id="salesperson">
                            {% set thisuser = frappe.get_doc("User", frappe.user) %}
                            {% if thisuser and thisuser.first_name and thisuser.first_name != 'None' %}
