                                {% set date_difference = frappe.utils.date_diff(thisserial.warranty_expiry_date,frappe.utils.today()) %}
                            {% endif %}
                            {% if thisserial.warranty_expiry_date and date_difference < 0 %}
                                {% set wstatus = "INVALID" %}
                            {% elif thisserial.warranty_expiry_date and date_difference > 0 %}
                                {% set wstatus = "VALID" %}
                            {% elif thisserial.warranty_expiry_date and date_difference == 0 %}
                                {% set wstatus = "EXPIRES TODAY" %}
                            {% else %}
                                {% set wstatus = 'UNKNOWN' %}
                            {% endif %}
                        {% else %}
                            {% set wstatus = "N/A" %}
                        {% endif %}
                        <tr class="nospace-row">
                            <td class="label-cell">
                                {% if doc.serial_number and wstatus %}Warranty Status:{% endif %}
                            </td>
                            <td class="data-cell">
                                {% if doc.serial_number and wstatus %}
                                    {% if wstatus == "INVALID" %}
                                        <span style="color: red;">
                                    {% elif wstatus == "VALID" %}
                                        <span style="color: green;">
                                    {% elif wstatus == "EXPIRES TODAY" %}
                                        <span style="color: orange;">
                                    {% else %}
                                        <span>
                                    {% endif %}
                                    {{ wstatus }}        
                                    </span>
                                {% endif %}
                            </td>
                            <td class="label-cell">
                                Item Group:
                            </td>   
                            <td class="data-cell">
                                {{ thisproduct.item_group }}
                            </td>
                        </tr>
                    </tbody></table>
