css:
@font-face {
	font-family: DaxlinePro;
	font-style: normal;
	font-weight: 300;
	src: url(/fonts/DaxlinePro.ttf);
}

@font-face {
	font-family: Neuropol;
	font-style: normal;
	font-weight: 300;
	src: url(/fonts/NeuropolXRG.ttf);
}

@font-face {
    font-family: 'Atlanta';
    src: url(/fonts/Atlanta-Book.woff2) format('woff2'),
        url(/fonts/Atlanta-Book.woff) format('woff'),
        url(/fonts/Atlanta-Book.ttf) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}

.print-format { 
    padding: 0mm !important;
    font-family: 'Atlanta';
}

body, p, span, div, element {
    color: #474c51;
    letter-spacing: normal !important;
}

#header-image {
    background-image: url(/files/Eskill_Header.jpg);
    display: table;
    width: 100%;
    background-size: 211mm;
    background-repeat: no-repeat;
    height: 18mm;
    background-position: left top;
    padding-top: 7mm !important;
}

#print-heading {
    font-family: DaxlinePro;
    font-size: 18pt;
    font-weight: bold;
    line-height: 1;
    padding: 0mm !important;
    margin-top: 4mm !important;
    margin-right: 6mm !important;
    margin-bottom: 8mm !important;
    text-align: right;
}

.section {
    margin-left: 7mm;
    margin-right: 7mm;
    display: table;
    border-collapse: separate;
    border-spacing: 1mm;
    width: 197mm !important;
    border-bottom: 1px solid black;
    padding-bottom: 10mm;
}

.layout-div {
    margin: 0 !important;
    padding: 0 !important;
    display: table-cell;
    vertical-align: top;
    padding-top: 10mm;
}

.black-box {
    border: 1px solid #474c51;
    border-radius: 3px;
    padding: 0.5mm;
    width: auto;
    height: auto;
    min-height: 12mm;
    display: table-cell;
}

#product-info {
    width: 117mm;
}

#warranty-status {
    margin: 10px 0px 0px 0px;
}

.under-warranty {
    background-color: green;
    border-radius: 4px;
    color: white;
    font-size: 12px;
    padding: 2px 6px;
    margin: 4px 0px 20px 0px;
}

.not-under-warranty {
    background-color: red;
    border-radius: 4px;
    color: white;
    font-size: 12px;
    padding: 2px 6px;
    margin: 4px 0px 20px 0px;
}

.expires-today {
    background-color: orange;
    border-radius: 4px;
    color: white;
    font-size: 12px;
    padding: 2px 6px;
    margin: 4px 0px 20px 0px;
}

.expiry-unknown {
    background-color: black;
    border-radius: 4px;
    color: white;
    font-size: 12px;
    padding: 2px 6px;
    margin: 4px 0px 20px 0px;
}

#product-status, #warranty-period, #product-warehouse {
    margin: 0mm;
}

#product-image {
    width: 80mm;
    text-align: right;
}

h1 {
    font-family: Atlanta;
    font-size: 16px;
    font-weight: bold;
    margin-bottom: 15px;
}

h2 {
    font-family: Atlanta;
    font-size: 12px;
    font-weight: bold;
    letter-spacing: 2px;
    margin-top: 0;
}

#history-line {
    font-size:8pt;
}

#footer-text {
    width: 100%;
    text-align: center;
}

.layout-table {
    width: 197mm;
}
.layout-row {
    border-bottom: 0.5px dotted #ffbc1f;
}

#date-cell {
    border-right: 4px solid #ffbc1f;
    width: 30mm;
    text-align: right;
}

html:
{% macro pageheader() %}

<div id="header-image" class="layout-div">
    <div id="print-heading">
        Serial Number Report
    </div>
</div>

{% endmacro %}

{% macro pagefooter() %}
<div id="footer-text">
    Report Generated on {{ frappe.utils.formatdate(frappe.utils.today(), 'dd-MM-YYYY') }} by {{ frappe.user }}
</div>

{% endmacro %}

{% set output_line = namespace(value=[]) %}
{% set installed = namespace(components=[]) %}





{{ pageheader() }}

<div class="section">
    <div class="layout-div" id="product-info">
        <h1>{{doc.item_name }}</h1><h2>{{ doc.serial_no }}</h2>

        {% if doc.status == "Delivered" %}

            {% if doc.warranty_expiry_date %}
                {% set date_difference = frappe.utils.date_diff(doc.warranty_expiry_date,frappe.utils.today()) %}
                {% if date_difference >= 0 %}Days remaining: {{ date_difference }}{% endif %}
            {% endif %}
            {% if doc.warranty_expiry_date and date_difference < 0 %}
                <p><span class="not-under-warranty">Not Under Warranty</span></p>
                <p id="warranty-status">Warranty expired on {{ frappe.utils.formatdate(doc.warranty_expiry_date) }}</p>

            {% elif doc.warranty_expiry_date and date_difference > 0 %}
                <p><span class="under-warranty">Under Warranty</span></p>
                <p id="warranty-status">Warranty expires on {{ frappe.utils.formatdate(doc.warranty_expiry_date) }}</p>

            {% elif doc.warranty_expiry_date and date_difference == 0 %}
                <p><span class="expires-today">Warranty Expires Today</span></p>
                <p id="warranty-status">Warranty expires on {{ frappe.utils.formatdate(doc.warranty_expiry_date) }}</p>

            {% else %}
                <p><span class="expiry-unknown">Unknown</span></p>
                <p id="warranty-status">Warranty expiry date not specified</p>

            {% endif %}
        {% endif %}
        <p id="product-status">Status: {{ doc.status }}</p>
        {% if doc.warehouse %}
            {% set current_warehouse = frappe.get_doc("Warehouse", doc.warehouse) %}
            <p id="product-warehouse">Location: {{ current_warehouse.warehouse_name }}</p>
        {% endif %}
        {% if doc.warranty_period %}<p id="warranty-period">Warranty Period: {{ doc.warranty_period }} days</p>{% else %}Warranty Period: Not Specified{% endif %}        
    </div>
    {% set thisproduct = frappe.get_doc("Item", doc.item_code) %}
    {% if thisproduct.image %}
        <div id="product-image">
            {% set display_image = '<img src=\"' ~ thisproduct.image ~ '\" style=\"max-width: 200px; max-height: 200px;\">' | safe %}
            {{ display_image }}
        </div>
    {% endif %}
</div>
<div class="section">
    <h2>Product History</h2>

        {# Get from Purchase Receipts #}

        {% set purchase_receipt_items = frappe.get_all('Purchase Receipt Item', filters=[['serial_no', 'like', '%'~doc.serial_no~'%']], fields=['name','docstatus','parent','qty','warehouse']) %}

        {% for pr_item in purchase_receipt_items %}
            {% set thispr = frappe.get_doc("Purchase Receipt", pr_item.parent) %}
            {% if thispr.docstatus != 2 and thispr.docstatus != 0 %}
                {% set textline %}
                    {% if not thispr.is_return %}
                        Received from {{ thispr.supplier_name }} on {{ pr_item.parent }}
                        {% if pr_item.project %}, linked to project "{{ pr_item.project }}"{% endif %}
                        {% if pr_item.qty == 1 %}, and was the only unit of this model.{% else %}, and was 1 of {{ pr_item.qty | int }} units of this model.{% endif %}
                        {% if pr_item.warehouse %}
                            {% set dest_warehouse = frappe.get_doc("Warehouse", pr_item.warehouse) %}
                            It was received into {{ dest_warehouse.warehouse_name }}.
                        {% endif %}
                    {% else %}
                        Returned to {{ thispr.supplier_name }} on {{ pr_item.parent }}
                        {% if pr_item.warehouse %}
                            {% set source_warehouse = frappe.get_doc("Warehouse", pr_item.warehouse) %}
                            , leaving from {{ source_warehouse.warehouse_name }}.
                        {% else %}
                            .
                        {% endif %}            
                    {% endif %}
                {% endset %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(thispr.posting_date), 'yyyy-MM-dd'), 'desc': textline }] %}            
            {% endif %}
        {% endfor %}


        {# Get from Stock Entries #}

        {% set stock_entry_details = frappe.get_all('Stock Entry Detail', filters=[['serial_no', 'like', '%'~doc.serial_no~'%']], fields=['name','docstatus','parent','qty','s_warehouse','t_warehouse','project']) %}
        {% for se_detail in stock_entry_details %}
            {% set thisse = frappe.get_doc("Stock Entry", se_detail.parent) %}
            {% if thisse.docstatus != 2 and thisse.docstatus != 0 %}
                {% set textline %}
                    {% if se_detail.s_warehouse %}{% set source_warehouse = frappe.get_doc("Warehouse", se_detail.s_warehouse) %}{% endif %}
                    {% if se_detail.t_warehouse %}{% set dest_warehouse = frappe.get_doc("Warehouse", se_detail.t_warehouse) %}{% endif %}
                    {% if se_detail.s_warehouse and se_detail.t_warehouse %}
                        Unit was transferred from {{ source_warehouse.warehouse_name }} to {{ dest_warehouse.warehouse_name }} on {{ se_detail.parent }}
                        {% if thisse.project %}
                            , linked to project "{{ thisse.project }}"
                        {% elif not thisse.project and se_detail.project %}
                            , linked to project "{{ se_detail.project }}"
                        {% endif %}
                        {% if se_detail.qty == 1 %}, and was the only unit of this model moved.{% else %}, and was 1 of {{ se_detail.qty | int }} units of this model moved.{% endif %}                    
                    {% elif se_detail.s_warehouse and not se_detail.t_warehouse %}
                       Delivered from {{ source_warehouse.warehouse_name }} on stock entry {{ se_detail.parent }}.
                    {% elif se_detail.t_warehouse and not se_detail.s_warehouse %}
                       Received into {{ dest_warehouse.warehouse_name }} on stock entry {{ se_detail.parent }}.
                    {% endif %}
                {% endset %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(thisse.posting_date), 'yyyy-MM-dd'), 'desc': textline }] %}            
            {% endif %}
        {% endfor %}


        {# Get from Delivery Notes #}

        {% set delivery_note_items = frappe.get_all('Delivery Note Item', filters=[['serial_no', 'like', '%'~doc.serial_no~'%']], fields=['name','docstatus','parent','qty','warehouse','reason_for_return']) %}

        {% for dnote_item in delivery_note_items %}
            {% set thisdnote = frappe.get_doc("Delivery Note", dnote_item.parent) %}
            {% if thisdnote.docstatus != 2 and thisdnote.status != 0 %}
                {% if not thisdnote.is_return %}
                    {% set textline %}
                        Delivered to {{ thisdnote.customer_name }} on {{ dnote_item.parent }}
                        {% if thisdnote.project %}, linked to project "{{ thisdnote.project }}"{% endif %}
                        {% if dnote_item.qty == 1 %}, and was the only unit of this model.{% else %}, and was 1 of {{ dnote_item.qty | int }} units of this model.{% endif %}
                        {% if dnote_item.warehouse %}
                            {% set source_warehouse = frappe.get_doc("Warehouse", dnote_item.warehouse) %}
                            Delivery was made from {{ source_warehouse.warehouse_name }}.
                        {% endif %}
                    {% endset %}
                {% else %}
                    {% set textline %}
                        Returned by {{ thisdnote.customer_name }} on {{ dnote_item.parent }}
                        {% if dnote_item.warehouse %}
                            {% set dest_warehouse = frappe.get_doc("Warehouse", dnote_item.warehouse) %}
                            , received into location {{ dest_warehouse.warehouse_name }}.
                        {% else %}
                            .
                        {% endif %}  
                        {% if dnote_item.reason_for_return %} Reason given for return: "{{ dnote_item.reason_for_return }}".{% endif %}
                    {% endset %}
                {% endif %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(thisdnote.posting_date), 'yyyy-MM-dd'), 'desc': textline }] %}            
            {% endif %}
        {% endfor %}        


        {# Get from Stock Recons positive #}

        {% set stock_recon_items = frappe.get_all('Stock Reconciliation Item', filters=[['serial_no', 'like', '%'~doc.serial_no~'%']], fields=['name','docstatus','parent','warehouse']) %}

        {% for sr_item in stock_recon_items %}
            {% set thissr = frappe.get_doc("Stock Reconciliation", sr_item.parent) %}
            {% if thissr.docstatus != 2 and thissr.docstatus != 0 %}
                {% if sr_item.warehouse %}{% set dest_warehouse = frappe.get_doc("Warehouse", sr_item.warehouse) %}{% endif %}
                    {% if thissr.purpose == "Opening Stock" %}
                        {% set textline %}
                            Brought into {% if dest_warehouse.warehouse_name %}{{ dest_warehouse.warehouse_name }}{% else %}unknown location{% endif %} as opening stock, on stock reconciliation {{ sr_item.parent }}.
                        {% endset %}
                    {% else %}
                        {% set textline %}
                            Confirmed in stock, in {% if dest_warehouse.warehouse_name %}{{ dest_warehouse.warehouse_name }}{% else %}unknown location{% endif %}, on stock reconciliation {{ sr_item.parent }}.
                        {% endset %}
                    {% endif %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(thissr.posting_date), 'yyyy-MM-dd'), 'desc': textline }] %}
            {% endif %}
        {% endfor %} 

        {# Get from Stock Recons negative #}

        {% set stock_recon_items = frappe.get_all('Stock Reconciliation Item', filters={'current_serial_no': ['like', '%'~doc.serial_no~'%'],'serial_no': ['not like', '%'~doc.serial_no~'%']}, fields=['name','docstatus','parent','warehouse']) %}

        {% for sr_item in stock_recon_items %}
            {% set thissr = frappe.get_doc("Stock Reconciliation", sr_item.parent) %}
            {% if thissr.docstatus != 2 and thissrdoc.status != 0 %}
                {% if sr_item.warehouse %}{% set dest_warehouse = frappe.get_doc("Warehouse", sr_item.warehouse) %}{% endif %}
                {% set textline %}
                    Removed from {% if dest_warehouse.warehouse_name %}{{ dest_warehouse.warehouse_name }}{% else %}unknown location{% endif %}, on stock reconciliation {{ sr_item.parent }}.
                {% endset %}
                                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(thissr.posting_date), 'yyyy-MM-dd'), 'desc': textline }] %}  
            {% endif %}
        {% endfor %} 


        {# Get from Issues #}

        {% set issues = frappe.get_all('Issue', filters={'serial_number': ['like', '%'~doc.serial_no~'%']}, fields=['name','subject','technician_name','customer','opening_date','service_level_agreement']) %}

        {% for issue in issues %}
            {% set textline %}
                Issue {{ issue.name }} was raised {% if issue.customer %}by {{ issue.customer }}{% else %}internally{% endif %}, with subject "{{ issue.subject }}". 
                {% if issue.technician_name and issue.service_level_agreement %}
                    It was attended to by {{ issue.technician_name }} under {{ issue.service_level_agreement }}.
                {% elif issue.technician_name and not issue.service_level_agreement and issue.customer %}
                    It was attended to by {{ issue.technician_name }}, and was not covered under any SLA.
                {% elif issue.technician_name and not issue.service_level_agreement and not issue.customer %}
                    It was attended to by {{ issue.technician_name }}.                
                {% elif not issue.technician_name and issue.service_level_agreement %}
                    It was covered under {{ issue.service_level_agreement }}, but no technician was listed under the issue.
                {% else %}
                    No technician was listed under the issue, and the device was not covered under any SLA.
                {% endif %}
            {% endset %}
            {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(issue.opening_date), 'yyyy-MM-dd'), 'desc': textline }] %}  
        {% endfor %}         

        {# Get from Warranty Claims #}

        {% set warranty_claims = frappe.get_all('Warranty Claim', filters={'serial_no': ['like', '%'~doc.serial_no~'%']}, fields=['name','docstatus','serial_no','complaint_date','complaint']) %}

        {% for wc_item in warranty_claims %}
            {% if wc_item.docstatus != 2 %}
                {% set textline %}
                    Warranty claim {{ wc_item.name }} was raised: {{ wc_item.complaint }}
                {% endset %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(wc_item.complaint_date), 'yyyy-MM-dd'), 'desc': textline }] %}  
            {% endif %}
        {% endfor %}


        {# Get from Warranty Swap Outs coming in #}

        {% set warranty_swap_outs = frappe.get_all('Warranty Swap Out', filters={'serial_no_in': ['like', '%'~doc.serial_no~'%']}, fields=['name','docstatus','serial_no_in','date','issue','customer','warranty_claim','serial_no_out']) %}

        {% for wso_item in warranty_swap_outs %}
            {% if wso_item.docstatus != 2 and wso_item.docstatus != 0 %}
                {% set textline %}
                    Warranty swap out {{ wso_item.name }} was done, with {{ wso_item.serial_no_in }} being returned 
                    and {{ wso_item.serial_no_out }} issued as a replacement
                    {% if wso_item.customer %} to {{ wso_item.customer }}.{% else %}.{% endif %}
                    {% if wso_item.issue %} Linked to {{ wso_item.issue }}.{% endif %}
                    {% if wso_item.warranty_claim %} Linked to {{ wso_item.warranty_claim }}.{% endif %}
                {% endset %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(wso_item.date), 'yyyy-MM-dd'), 'desc': textline }] %}  
            {% endif %}
        {% endfor %}         


        {# Get from Warranty Swap Outs going out #}

        {% set warranty_swap_outs = frappe.get_all('Warranty Swap Out', filters={'serial_no_out': ['like', '%'~doc.serial_no~'%']}, fields=['name','docstatus','serial_no_in','date','issue','customer','warranty_claim','serial_no_out']) %}

        {% for wso_item in warranty_swap_outs %}
            {% if wso_item.docstatus != 2 and wso_item.docstatus != 0 %}
                {% set textline %}
                    Warranty swap out {{ wso_item.name }} was done, with {{ wso_item.serial_no_in }} being returned 
                    and {{ wso_item.serial_no_out }} issued as a replacement
                    {% if wso_item.customer %} to {{ wso_item.customer }}.{% else %}.{% endif %}
                    {% if wso_item.issue %} Linked to {{ wso_item.issue }}.{% endif %}
                    {% if wso_item.warranty_claim %} Linked to {{ wso_item.warranty_claim }}.{% endif %}
                {% endset %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(wso_item.date), 'yyyy-MM-dd'), 'desc': textline }] %}  
            {% endif %}
        {% endfor %}

        {# Get from Component History (as component) #}

        {% set component_history = frappe.get_all('Component History', filters=[['serial_no', 'like', '%'~doc.serial_no~'%']], fields=['name','date','docstatus','parent','action']) %}

        {% for ch_item in component_history %}
            {% set thisparent = frappe.get_doc("Serial No", ch_item.parent) %}
                {% set textline %}
                    {% if ch_item.action == "Installed" %}
                        Installed as component in {{ thisparent.item_name }} with serial number {{ thisparent.name }}. 
                    {% elif ch_item.action == "Uninstalled" %}
                        Uninstalled as component from {{ thisparent.item_name }} with serial number {{ thisparent.name }}.           
                    {% endif %}
                {% endset %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(ch_item.date), 'yyyy-MM-dd'), 'desc': textline }] %}            
        {% endfor %}

        {# Get from Component History (as parent) #}

        {% set component_history = frappe.get_all('Component History', filters=[['parent', 'like', '%'~doc.serial_no~'%']], fields=['name','date','serial_no','docstatus','parent','action']) %}

        {% for ch_item in component_history %}
                {% set cserial = frappe.get_doc("Serial No", ch_item.serial_no) %}
                {% if ch_item.serial_no not in installed.components %}
                    {% set installed.components = installed.components + [ch_item.serial_no] %}
                {% endif %}
                {% set textline %}
                    {% if ch_item.action == "Installed" %}
                        <span style="color: green;">&#8594;</span> {{ cserial.item_name }} was installed (SN: {{ ch_item.serial_no }}).
                    {% elif ch_item.action == "Uninstalled" %}
                        <span style="color: red;">&#8592;</span> {{ cserial.item_name }} with serial number {{ ch_item.serial_no }} was <strong>removed</strong>.
                    {% endif %}
                {% endset %}
                {% set output_line.value = output_line.value + [{'date': frappe.utils.formatdate(frappe.utils.get_datetime(ch_item.date), 'yyyy-MM-dd'), 'desc': textline }] %}            
        {% endfor %}


    <table class="layout-table">
        <tbody>
            {% for tline in output_line.value | sort(attribute='date') %}
                <tr class="layout-row">
                    <td class="layout-cell" id="date-cell">
                        {% if loop.previtem is not defined or loop.previtem.date != tline.date %}
                            <strong>{{ frappe.utils.formatdate(frappe.utils.get_datetime(tline.date), 'dd-MM-yyyy') }}</strong>
                        {% endif %}
                    </td>
                    <td class="layout-cell" id="history-line">{{ tline.desc }}</td>

                </tr>
            {% endfor %}  
        </tbody>
    </table>
</div>

        {# Final check of what components should be installed #}

        {% if installed.components %}
            {% set component_line = namespace(value=[]) %}
            {% set action_count = namespace(value=0) %}
            {% for component in installed.components %}
                {% set thishistory = frappe.get_all('Component History', filters=[['parent', 'like', '%'~doc.serial_no~'%'],['serial_no', '=', component]], fields=['name','date','serial_no','docstatus','parent','action']) %}
                {% set action_count.value = 0 %}
                {% for thisitem in thishistory %}
                    {% if thisitem.action == "Installed" %}
                        {% set action_count.value = action_count.value + 1 %}
                    {% elif thisitem.action == "Uninstalled" %}
                        {% set action_count.value = action_count.value - 1 %}
                    {% endif %}
                {% endfor %}

                {% if action_count.value > 0 %}
                    {% set cserial = frappe.get_doc("Serial No", component) %}
                    {% set component_line.value = component_line.value + [{'group': cserial.item_group, 'code': cserial.item_code, 'serial': cserial.serial_no }] %}            
                {% endif %}
            {% endfor %}

            <div class="section">
                <h2>Currently Installed Components</h2>
                <table class="layout-table">
                    <tbody>
                        {% for cline in component_line.value | sort(attribute='group') %}
                            <tr class="layout-row">
                                <td class="layout-cell" id="date-cell">
                                    {% if loop.previtem is not defined or loop.previtem.group != cline.group %}
                                        <strong>{{ cline.group }}</strong>
                                    {% endif %}
                                </td>
                                <td class="layout-cell">
                                    {{ cline.code }}
                                </td>
                                <td class="layout-cell">
                                    {{ cline.serial }}
                                </td>
                            </tr>
                        {% endfor %}
                    </tbody>
                </table>
            </div>
        {% endif %}




{{ pagefooter() }}
