css:
@media screen {
	.print-format {
		width: 210mm;
		height: 297mm;
	}
}

@media print {
    #header-image {
        position: inherit;
    }
}

.print-format { 
    padding: 0mm !important;
    line-height: 150%;
}

.print-format table, .print-format tr, .print-format td, .print-format div, .print-format p {
	line-height: 150%;
	vertical-align: middle;
}

body, p, span, div, element {
    color: #474c51;
    letter-spacing: normal !important;
}

#header-image {
    /*background-image: url(/files/Eskill_Header.jpg);*/
    display: table;
    width: 100%;
    background-size: 211mm;
    background-repeat: no-repeat;
    height: 38mm;
    background-position: left top;
    padding-top: 7mm !important;
    margin-left: 7mm;
    margin-right: 7mm;
}

#print-heading {
    font-size: 18pt;
    font-weight: bold;
    line-height: 1;
    padding-right: 5mm !important;
    margin-top: 4mm !important;
    margin-right: 6mm !important;
    margin-bottom: 3mm !important;
    text-align: right;
}

.layout-table, .layout-row, .layout-div {
    margin: 0 !important;
    padding: 0 !important;
    width: 100% !important;
}

#middle-section {
    margin-left: 7mm;
    margin-right: 7mm;
}

#middle-section-div {
    min-height: 12cm;
    height: 12cm;
}

.column-header {
    text-align: center;
    font-weight: bold;
    background-color: #e0e0e0;
}

.blank-row {
    background-color: #e7e7e7;    
}

.table-cell {
    font-size: 11px;
    border-right: solid 1px black;
}

.table-cell-right {
    font-size: 11px;
    border-right: none;
}

.table-row {
    border-bottom: solid 0.5px black;
}

table {
    width: 100%;
}

#footer {
    display: table;
    width: 100%;
    background-image: url(/files/Eskill_Footer.jpg);
    background-size: 211mm;
    background-repeat: no-repeat;
    height: 24mm;
    background-position: left bottom;
}
