css:
.print-format { 
    padding: 0mm !important;
}

body, p, span, div, element {
    color: #474c51;
    letter-spacing: normal !important;
}

#header-image {
    /*background-image: url(/files/Eskill_Header.jpg);*/
    display: table;
    width: 100%;
    background-size: 211mm;
    background-repeat: no-repeat;
    height: 38mm;
    background-position: left top;
    padding-top: 7mm !important;
    margin-left: 7mm;
    margin-right: 7mm;
}

.layout-table, .layout-row, .layout-div {
    margin: 0 !important;
    padding: 0 !important;
    width: 100% !important;
}

.layout-cell {
    margin: 0 !important;
    padding: 0 !important;
    height: 100%;
    vertical-align: top;
}



#print-heading {
    font-size: 18pt;
    font-weight: bold;
    line-height: 1;
    padding: 0mm !important;
    margin-top: 4mm !important;
    margin-right: 6mm !important;
    margin-bottom: 3mm !important;
    text-align: right;
}

#middle-section {
    margin-left: 7mm;
    margin-right: 7mm;
}

#middle-section-div {
    min-height: 12cm;
    height: 12cm;
}

.grey-head {
    background-color: #e0e0e0;
    width: 100%;
    text-align: center;
    line-height: 1.5;
    font-size: 7pt;
    font-weight: bold;
    color: black;
    padding: 0 5px 0 5px;
}

#items {
    table-layout: fixed;
}

#item {
    padding: 0 !important;
    padding-left: 5px;
    height: auto;
}

#item div { 
    overflow: hidden;
    max-height: 12pt;
    height: 12pt;
}

.cell-content-center {
    width: 100%;
    text-align: center;
    line-height: 1.5;
    font-size: 8pt !important;
    font-weight: normal;
    padding: 0 5px 0 5px;
}

.cell-content-left {
    width: 100%;
    text-align: left;
    line-height: 1.5;
    font-size: 8pt !important;
    font-weight: normal;
    padding: 0 5px 0 5px;
}

.cell-content-right {
    width: 100%;
    text-align: right;
    line-height: 1.5;
    font-size: 8pt !important;
    font-weight: normal;
    padding: 0 5px 0 5px;
}

#footer {
    display: table;
    width: 100%;
    background-image: url(/files/Eskill_Footer.jpg);
    background-size: 211mm;
    background-repeat: no-repeat;
    height: 24mm;
    background-position: left bottom;
}
